import React, { useEffect, useState } from 'react'
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';

import Typography from '@mui/material/Typography';


import Button from '@mui/material/Button';
import { Link,  useNavigate} from 'react-router-dom';
import { Menu, MenuItem } from '@mui/material';

function Navbar() {

  const [scrollPosition, setScrollPosition] = useState(0);
  const [anchorEl, setAnchorEl] = useState(null);
  const [userName, setUserName] = useState(localStorage.getItem('username'));

  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  useEffect(() => {
    const handleScroll = () => {
      const position = window.pageYOffset;
      setScrollPosition(position);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('email');
    localStorage.removeItem('username');
    setIsAuthenticated(false);
    setUserName(null);
  };

  const landingPageHeight = 580; // Adjust this value based on your landing page height
  const isLandingPage = scrollPosition < landingPageHeight;
  const navbarColor = isLandingPage ? 'rgba(0, 0, 0, 0)' : '#000000';


 
  return (
    <div>
         <Box sx={{ flexGrow: 1}}>
      <AppBar position="fixed"sx={{   backgroundColor: navbarColor, transition: 'background-color 0.3s ease'  }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
         
          <Typography variant="h6" component="div" >
            Drivemate
          </Typography>
        
          {userName ? (
                // If user is logged in, display the user's name
                <div>
                  <Button
                    color="error"
                    aria-controls="simple-menu"
                    aria-haspopup="true"
                    onClick={handleClick}
                  >
                    {userName}
                  </Button>
                  <Menu
                  
                    id="simple-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={handleClose}
                    
                  >
                   <Link to={"/booking"} style={{ textDecoration: 'none' ,color:"black"}}> <MenuItem >Profile</MenuItem></Link>
                    <MenuItem onClick={handleClose}>My account</MenuItem>
                    <MenuItem onClick={handlelogout}>Logout</MenuItem>
                  </Menu>
                </div>
                 ) : (
                  <>
        <Link to='/login'>  <Button color="inherit">Login</Button> </Link>
        </>
          )}
        </Toolbar>
      </AppBar>
    </Box>
    </div>
  )
}

export default Navbar
