import React, { useEffect, useState } from 'react'
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';

import Typography from '@mui/material/Typography';


import Button from '@mui/material/Button';

function Navbar() {
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const position = window.pageYOffset;
      setScrollPosition(position);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  const landingPageHeight = 630; // Adjust this value based on your landing page height
  const isLandingPage = scrollPosition < landingPageHeight;
  const navbarColor = isLandingPage ? 'rgba(0, 0, 0, 0)' : '#000000';


 
  return (
    <div>
         <Box sx={{ flexGrow: 1}}>
      <AppBar position="fixed"sx={{   backgroundColor: navbarColor, transition: 'background-color 0.3s ease'  }}>
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
         
          <Typography variant="h6" component="div" >
            Drivemate
          </Typography>
          <Button color="inherit">Login</Button>
        </Toolbar>
      </AppBar>
    </Box>
    </div>
  )
}

export default Navbar
